INSERT INTO syllabus_data(ID,講義名,教員名,授業概要,単位数,使用言語,授業の実施形式) VALUES (34,'データと計量経済学','中西 崇文','ベイズ統計は計量経済学や現在の機械学習の考え方の根本をなす分野である。例えば、推計統計学の考え方では、今ある標本データをまとめて分析するのに対し、ベイズ統計では新たなデータを付け加えた場合、そのデータによって更新をしていくことができる。これは今日の機械学習の考え方の基本となる。本講義では、いわゆる「ベイジアン」の基礎を論じていくと同時に、実際にPython、PyMC3を用いて具体的な活用シーンを示す。',2,'日本語','オンライン授業');
