INSERT INTO syllabus_data(ID,講義名,教員名,授業概要,単位数,使用言語,授業の実施形式) VALUES (36,'複合現実','岩田 洋夫','複合現実(MR:Mixed Reality)は、1980年代にスタートしたVR(Virtual Reality)研究が発展する過程で、1990年代に提案された言葉である。現実世界とバーチャル世界を融合する、多様な手法を統合した概念がMRである。本講義ではMRの根底にあるVRの基礎知識を説明し、その応用分野を紹介する。これらの知識に基づいて、課題発見とソリューション提案をする、グループワークとプレゼンテーションを行う。',2,'日本語','オンライン授業');
