INSERT INTO syllabus_data(ID,講義名,教員名,授業概要,単位数,使用言語,授業の実施形式) VALUES (38,'機械学習アルゴリズムデザイン','福原 義久','機械学習とは、コンピュータがデータに含まれる特徴を反復的に学習することにより分類、認識、検知を実現するデータ分析技術である。近年では、複数の機械学習法を組み合わせて使うアンサンブル学習や、さまざまな種類の深層学習モデルがさまざまな分野で活用されている。本講義では、これらの近年よく用いられているいくつかのアプローチ（アンサンブル機会学習、ＣＮＮ、ＲＮＮ等）について、実際にモデルを組みながら、その仕組みについての理解を深める。',2,'日本語','オンライン授業');
