INSERT INTO syllabus_data(ID,講義名,教員名,授業概要,単位数,使用言語,授業の実施形式) VALUES (42,'人工知能AIデザイン演習2','長谷川 理','ビッグデータ、メディア・データを対象にその蓄積、共有、統合、想起、配信、そして応用を実現するためには、プログラミング技能が不可欠である。本演習では、「人工知能（AI）デザイン演習Ⅰ」の授業で学んだことを踏まえながらPythonを用いて、多層構造のニューラルネットワークを使った機械学習手法について演習を行っていく。具体的には，ニューラルネットワークの構造を学ぶ上で、パーセプトロン、フォワード・プロパゲーション、逆伝搬誤差法等について学ぶ。。',1,'日本語','オンライン授業');
